// export default Todo;
import React from 'react';

function Todo({ todo, index }) {
  function todoDeleteHandler(index) {
    todo.find((_, idx) => !index === idx);
  }

  return <div onDoubleClick={todoDeleteHandler}>{todo}</div>;
}

export default Todo;
